const request = require('request');

let url;

const info = (username, callback, error) => {
    url = `http://roblox.reforgedmc.ml/api/${username}/`;
    request(url, { json: true }, (err, res, body) => {
        if(err) {
            return error(err);
        }
        return callback(body);
    });
}

module.exports = info;